mapper.select:
select materia.materia_id, materia.materia_nombre as m_nombre, materia.materia_nemonico as m_nemonico, materia.creditos as m_creditos, materia.horas_monitoria_curso as m_horas_monitoria_curso,
tema.temaid, tema.topic as tm_topic, tema.descripcion as tm_descripcion,
grupo.grupoid, grupo.grupo_nombre as gr_grupo_nombre, grupo.numeroestudiante as gr_numeroestudiante, grupo.horario_monitor as gr_horario_monitor,
profesor.profesorid, profesor.nombres as pr_nombres, profesor.apellidos as pr_apellidos, profesor.correo as pr_correo, profesor.telefono as pr_telefono,
semestre.semestre_id, semestre."year" as se_year, semestre.periodo_academico as se_periodo_academico, semestre.fechainicio as se_fechainicio, semestre.fechafin as se_fechafin,
monitor.codigo_id, monitor.nombres as mo_nombres, monitor.apellidos as mo_apellidos, monitor.correo as mo_correo, monitor.telefono as mo_telefono, monitor.semestreingreso as mo_semestreingreso, monitor.programa_academico as mo_programa_academico
from materia join grupo on (materia_id=grupo.materia_materia_id) join tema on (materia_id=tema.materia_materia_id) join profesor on (profesorid=profesor_profesor_id) join semestre on (semestre.semestre_id=grupo.semestre_semestre_id) join monitor on (monitor.codigo_id=grupo.monitor_codigo_id)
materia.materia_id = #{MId}
AND grupo.grupoid = #{grdId}
AND tema.temaid = #{tmId}
