mapper.select:
select horario.horarioid, horario.descripcion, materia.materia_id, materia.materia_nombre as m_nombre, materia.materia_nemonico as m_nemonico, materia.creditos as m_creditos, materia.horas_monitoria_curso as m_horas_monitoria_curso,
FROM materia join horario on (horario.horarioid = materia.materia_id) join tema on (tema.materia_materia_id = materia.materia_id)
where materia.materia_id = #{codId}
